mapper.update:
UPDATE community_board
SET hit = hit + 1
WHERE cb_no = #{cb_no};

mapper.select:
SELECT COUNT(*)
FROM community_board_good
WHERE cb_no = #{cb_no} AND  m_no = #{m_no};

mapper.update:
UPDATE community_board_reply
SET content = #{content}
WHERE m_no = #{m_no};

UPDATE community_board
SET title = #{title}, content = #{content}, classify = #{classify}
WHERE cb_no = #{cb_no};
