mapper.sql:
WHERE oli.olt_no = olt.olt_no AND oli.oli_no = oltag.oli_no AND enable = 1 AND title LIKE '%${keyword}%'
AND (oltag.tag = 'JAVA' OR oltag.tag = 'C' OR oltag.tag = 'HTML' OR oltag.tag = 'C#' OR oltag.tag = 'Javascript' OR oltag.tag = 'Python' OR oltag.tag = 'CSS' OR oltag.tag = 'PHP' OR oltag.tag = 'Ruby' OR oltag.tag = 'C++')
AND oltag.tag = #{tag}
AND oltag.tag = #{tagParam}

mapper.select:
SELECT COUNT(*)
FROM online_lecture_like
WHERE oli_no = #{oli_no}

mapper.insert:
INSERT INTO online_lecture_like (oli_no, m_no)
SELECT #{oli_no}, #{m_no}
WHERE NOT EXISTS (SELECT m_no FROM online_lecture_like WHERE oli_no = #{oli_no} AND m_no = #{m_no})

mapper.select:
SELECT *
FROM online_lecture_introduction
WHERE enable = 1 AND oli_no = #{oli_no}
