mapper.update:
UPDATE note 
SET title = #{title},
	content = #{content},
	price = #{price},
	enable = #{enable}
WHERE n_no = #{n_no}

mapper.select:
SELECT COUNT(*)
FROM note_like
WHERE n_no = #{n_no}

mapper.update:
UPDATE note_article
SET title = #{title}, content = #{content}
WHERE na_no = #{na_no}
