mapper.select:
select m_id 
from member
where m_id = #{m_id}

select *
from member
where m_id = #{m_id} and m_pw = #{m_pw}

mapper.update:
update member
	set	img_path = #{img_path}, m_phone = #{m_phone}
where m_id = #{m_id} and m_pw = #{m_pw}

mapper.select:
select count(hol.oli_no)
from history_order_lecture hol
where hol.m_no = #{m_no}

select count(hon.hon_no)
from history_order_note hon
where hon.m_no = #{m_no}

select count(cb_no)
from community_board
where m_no = #{m_no}

SELECT m_name
FROM member
WHERE m_no = #{m_no}
